targets.query:
from(bucket: "k6")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "checks" and r._field == "value")
  |> filter(fn: (r) => r["testName"] =~ /${testName:regex}/)
  // Группируем по названию проверки и сценарию
  |> group(columns: ["check", "scenario"])
  // Считаем общее число проверок и число успешных
  |> reduce(
      identity: {total: 0.0, passed: 0.0},
      fn: (r, accumulator) => ({
        total: accumulator.total + 1.0,
        passed: accumulator.passed + r._value
      })
    )
  // Переводим во «читаемые» поля и вычисляем неуспехи и процент успеха
  |> map(fn: (r) => ({
       check:         r.check,
       scenario:      r.scenario,
       success_count: int(v: r.passed),
       fail_count:    int(v: r.total - r.passed),
       success_rate:  if r.total > 0.0 then (r.passed / r.total) * 100.0 else 0.0
     })
  )
  // Оставляем только нужные колонки в нужном порядке
  |> keep(columns: ["check","scenario","success_rate","success_count","fail_count"])
